CREATE TABLE phpbb_forums (
  forum_id int(10) NOT NULL,
  parent_id int(10) NOT NULL DEFAULT 0,
  left_id int(10) NOT NULL DEFAULT 0,
  right_id int(10) NOT NULL DEFAULT 0,
  forum_parents mediumtext NOT NULL DEFAULT '',
  forum_name varchar(255) NOT NULL DEFAULT '',
  forum_desc text NOT NULL DEFAULT '',
  forum_desc_bitfield varchar(255) NOT NULL DEFAULT '',
  forum_desc_options int(10) NOT NULL DEFAULT 7,
  forum_desc_uid varchar(8) NOT NULL DEFAULT '',
  forum_link varchar(255) NOT NULL DEFAULT '',
  forum_password varchar(255) NOT NULL DEFAULT '',
  forum_style int(10) NOT NULL DEFAULT 0,
  forum_image varchar(255) NOT NULL DEFAULT '',
  forum_rules text NOT NULL DEFAULT '',
  forum_rules_link varchar(255) NOT NULL DEFAULT '',
  forum_rules_bitfield varchar(255) NOT NULL DEFAULT '',
  forum_rules_options int(10) NOT NULL DEFAULT 7,
  forum_rules_uid varchar(8) NOT NULL DEFAULT '',
  forum_topics_per_page smallint(5) NOT NULL DEFAULT 0,
  forum_type smallint(6) NOT NULL DEFAULT 0,
  forum_status smallint(6) NOT NULL DEFAULT 0,
  forum_last_post_id int(10) NOT NULL DEFAULT 0,
  forum_last_poster_id int(10) NOT NULL DEFAULT 0,
  forum_last_post_subject varchar(255) NOT NULL DEFAULT '',
  forum_last_post_time int(10) NOT NULL DEFAULT 0,
  forum_last_poster_name varchar(255) NOT NULL DEFAULT '',
  forum_last_poster_colour varchar(6) NOT NULL DEFAULT '',
  forum_flags smallint(6) NOT NULL DEFAULT 32,
  display_on_index tinyint(1) NOT NULL DEFAULT 1,
  enable_indexing tinyint(1) NOT NULL DEFAULT 1,
  enable_icons tinyint(1) NOT NULL DEFAULT 1,
  enable_prune tinyint(1) NOT NULL DEFAULT 0,
  prune_next int(10) NOT NULL DEFAULT 0,
  prune_days int(10) NOT NULL DEFAULT 0,
  prune_viewed int(10) NOT NULL DEFAULT 0,
  prune_freq int(10) NOT NULL DEFAULT 0,
  display_subforum_list tinyint(1) NOT NULL DEFAULT 1,
  display_subforum_limit tinyint(1) NOT NULL DEFAULT 0,
  forum_options int(10) NOT NULL DEFAULT 0,
  forum_posts_approved int(10) NOT NULL DEFAULT 0,
  forum_posts_unapproved int(10) NOT NULL DEFAULT 0,
  forum_posts_softdeleted int(10) NOT NULL DEFAULT 0,
  forum_topics_approved int(10) NOT NULL DEFAULT 0,
  forum_topics_unapproved int(10) NOT NULL DEFAULT 0,
  forum_topics_softdeleted int(10) NOT NULL DEFAULT 0,
  enable_shadow_prune tinyint(1) NOT NULL DEFAULT 0,
  prune_shadow_days int(10) NOT NULL DEFAULT 7,
  prune_shadow_freq int(10) NOT NULL DEFAULT 1,
  prune_shadow_next int(11) NOT NULL DEFAULT 0,
  PRIMARY KEY (forum_id)
);
