CREATE TABLE phpbb_topics (
  topic_id int(10) NOT NULL,
  forum_id int(10) NOT NULL DEFAULT 0,
  icon_id int(10) NOT NULL DEFAULT 0,
  topic_attachment tinyint(1) NOT NULL DEFAULT 0,
  topic_reported tinyint(1) NOT NULL DEFAULT 0,
  topic_title varchar(255) NOT NULL DEFAULT '',
  topic_poster int(10) NOT NULL DEFAULT 0,
  topic_time int(10) NOT NULL DEFAULT 0,
  topic_time_limit int(10) NOT NULL DEFAULT 0,
  topic_views int(10) NOT NULL DEFAULT 0,
  topic_status smallint(6) NOT NULL DEFAULT 0,
  topic_type smallint(6) NOT NULL DEFAULT 0,
  topic_first_post_id int(10) NOT NULL DEFAULT 0,
  topic_first_poster_name varchar(255) NOT NULL DEFAULT '',
  topic_first_poster_colour varchar(6) NOT NULL DEFAULT '',
  topic_last_post_id int(10) NOT NULL DEFAULT 0,
  topic_last_poster_id int(10) NOT NULL DEFAULT 0,
  topic_last_poster_name varchar(255) NOT NULL DEFAULT '',
  topic_last_poster_colour varchar(6) NOT NULL DEFAULT '',
  topic_last_post_subject varchar(255) NOT NULL DEFAULT '',
  topic_last_post_time int(10) NOT NULL DEFAULT 0,
  topic_last_view_time int(10) NOT NULL DEFAULT 0,
  topic_moved_id int(10) NOT NULL DEFAULT 0,
  topic_bumped tinyint(1) NOT NULL DEFAULT 0,
  topic_bumper int(10) NOT NULL DEFAULT 0,
  poll_title varchar(255) NOT NULL DEFAULT '',
  poll_start int(10) NOT NULL DEFAULT 0,
  poll_length int(10) NOT NULL DEFAULT 0,
  poll_max_options smallint(6) NOT NULL DEFAULT 1,
  poll_last_vote int(10) NOT NULL DEFAULT 0,
  poll_vote_change tinyint(1) NOT NULL DEFAULT 0,
  topic_visibility smallint(6) NOT NULL DEFAULT 0,
  topic_delete_time int(10) NOT NULL DEFAULT 0,
  topic_delete_reason varchar(255) NOT NULL DEFAULT '',
  topic_delete_user int(10) NOT NULL DEFAULT 0,
  topic_posts_approved int(10) NOT NULL DEFAULT 0,
  topic_posts_unapproved int(10) NOT NULL DEFAULT 0,
  topic_posts_softdeleted int(10) NOT NULL DEFAULT 0,
  PRIMARY KEY (topic_id)
);
